template.value:
import { shallowMount } from '@vue/test-utils';
import { Store } from 'vuex-mock-store';
import $filename$ from './$filename$';

describe('$filename$', () => {
    let store;
    let mocks;
    let wrapper;

    beforeEach(() => {
        store = new Store({ state: {} });
        mocks = { $store: store };
        wrapper = shallowMount($filename$, { mocks })
    });

    it('should find the component name', () => {
        expect(wrapper.vm.$options.name).toBe('$filename$');
    });
});
